mapper.select:
select mb_id, mb_pw, mb_name, mb_nick from tb_member
where mb_id = #{mb_id}
and mb_pw = #{mb_pw}

mapper.insert:
insert into tb_reply(
	rv_seq,
	rp_content,
	mb_id
) values (
	#{rv_seq},		
	#{rp_content},
	#{mb_id}
)

mapper.delete:
delete from tb_likes
 where mb_id = #{mb_id}
 and   rv_seq = #{rv_seq})

delete from tb_follow
 where follower = #{follower}
 and   followee = #{followee})

mapper.select:
select * from tb_review
where mb_id = #{mb_id}
   or res_seq = #{res_seq}
   or rv_hashtag = #{rv_hashtag}

select * from tb_review
where mb_id = #{mb_id}
   or rv_hashtag = #{rv_hashtag}
